import React, { useState } from "react";
import GuestList from './GuestList'
// import axios from "axios";

const initform = {
  name1: "",
  name2: "",
};

const Rsvp = (props) => {
  const { setGuests, guests } = props;

  const [formValues, setFormValues] = useState(initform);

  const handleChange = (e) => {
    setFormValues({ [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // axios.post("url", formValues)
    //   .then((res) => {
    //     setGuests(res.data)
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });
    setGuests(formValues);
    setFormValues(initform);
  };

  return (
    <div className='rsvp'>
      <form className=''
        onSubmit={handleSubmit}>
        <input
          name="name1"
          value={formValues.name1}
          placeholder="Name"
          type="text"
          onChange={handleChange}
        />
        <br />
        <input
          name="name2"
          value={formValues.name2}
          placeholder="Name"
          type="text"
          onChange={handleChange}
        />
        <br />
        <button>RSVP</button>
      </form>
      <GuestList guests={guests}/>
    </div>
  );
};

export default Rsvp;
