    setPerson2(initform);
  };

  return (
    <div className="rsvp">
      <form className="form" onSubmit={handleSubmit}>
        <div className='inputsdiv'>
          <div className='inputdiv'>
            <input
              name="name1"
              value={formValues.name1}
              placeholder="Name"
              type="text"
              onChange={handleChange1}
            />
            <br />
            <input
              name="email1"
              value={formValues.email1}
              placeholder="Email"
              type="text"
              onChange={handleChange1}
            />
          </div>
          <div>
            <input
              name="name2"
              value={formValues.name2}
              placeholder="Name"
              type="text"
              onChange={handleChange2}
            />
            <br />
            <input
              name="email2"
              value={formValues.email2}
              placeholder="Email"
              type="text"
              onChange={handleChange2}
            />
          </div>
        </div>
        <button>RSVP</button>
      </form>
      <GuestList guests={guests} />
    </div>
  );
};

export default Rsvp;
