import React, { useState } from "react";
import axios from "axios";

const initform = {
  name: "",
  email: "",
};

const RsvpForm = (props) => {
  const [person1, setPerson1] = useState(initform);
  const [person2, setPerson2] = useState(initform);

  // handle function for person 1 form
  const handleChange1 = (e) => {
    setPerson1({ [e.target.name]: e.target.value });
  };
  // handle function for person 2 form
  const handleChange2 = (e) => {
    setPerson2({ [e.target.name]: e.target.value });
  };

  // same submit function for both
  const handleSubmit = (e) => {
    e.preventDefault();
    // Example of double axios request (can be done with post as well)
    // axios.all([
    //   axios.get('https://api.github.com/users/hacktivist123'),
    //   axios.get('https://api.github.com/users/adenekan41')
    // ])
    // .then(response => {
    //   console.log('Date created: ', response[0].data.created_at);
    //   console.log('Date created: ', response[1].data.created_at);
    // });

    // axios.all([
    //   axios.post("https://barcelona22.herokuapp.com/rsvps", person1),
    //   axios.post("https://barcelona22.herokuapp.com/rsvps", person2)
    // ])
    // .then(res => {
    //   console.log(res.data)
    // })
    // .catch(err => {
    //   console.log(err)
    // })

    // post person1
    // axios
    //   .post("https://barcelona22.herokuapp.com/rsvps", person1)
    //   .then((res) => {
    //     console.log(res.data);
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });
    // // post person2
    // axios
    //   .post("https://barcelona22.herokuapp.com/rsvps", person2)
    //   .then((res) => {
    //     console.log(res.data);
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });

    // reset form values
    setPerson1(initform);
    setPerson2(initform);
  };

  console.log(person1)

  return (
    <div className="rsvp">
      <form className="form" onSubmit={handleSubmit}>
        <div className="inputsdiv">
          <div className="inputdiv">
            <input
              name="name"
              value={person1.name}
              placeholder="Name"
              type="text"
              onChange={handleChange1}
            />
            <br />
            <input
              name="email"
              value={person1.email}
              placeholder="Email"
              type="text"
              onChange={handleChange1}
            />
          </div>
          <div>
            <input
              name="name"
              value={person2.name}
              placeholder="Name"
              type="text"
              onChange={handleChange2}
            />
            <br />
            <input
              name="email"
              value={person2.email}
              placeholder="Email"
              type="text"
              onChange={handleChange2}
            />
          </div>
        </div>
        <button>RSVP</button>
      </form>
    </div>
  );
};

export default RsvpForm;
