import React, {useState} from 'react'
import axios from 'axios'

const initform = {
    name1: '',
    name2: '',
}

const Rsvp = props => {
    const [formValues, setFormValues] = useState(initform)

    const handleChange = e => {
        setFormValues({[e.target.name]: e.target.value})
    }

    const handleSubmit = e => {
        e.preventDefault()
        axios.post()

    }

    return(
        <div>
            <form onSubmit={handleSubmit}>
                <input
                    name='name1'
                    value={formValues.name1}
                    placeholder='Name'
                    type='text'
                    onChange={handleChange}
                />
                <br />
                <input
                    name='name2'
                    value={formValues.name2}
                    placeholder='Name'
                    type='text'
                    onChange={handleChange}
                />
                <br />
                <button>RSVP</button>
            </form>
        </div>
    )
}

export default Rsvp;
