import React, { useState } from "react";
import GuestList from "./GuestList";
import axios from "axios";

const initform = {
  name: "",
  email: "",
};

const Rsvp = (props) => {
  const { setGuests, guests } = props;

  const [person1, setPerson1] = useState(initform)
  const [person2, setPerson2] = useState(initform)

  // handle function for person 1 form
  const handleChange1 = (e) => {
    setPerson1({ [e.target.name]: e.target.value });
  };
  // handle function for person 2 form
  const handleChange2 = (e) => {
    setPerson2({ [e.target.name]: e.target.value });
  };

  // same submit function for both
  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('url', person1)
      .then()

    // reset form values
    setPerson1(initform);
    setPerson2(initform);
  };

  return (
    <div className="rsvp">
      <form className="form" onSubmit={handleSubmit}>
        <div className='inputsdiv'>
          <div className='inputdiv'>
            <input
              name="name1"
              value={person1.name}
              placeholder="Name"
              type="text"
              onChange={handleChange1}
            />
            <br />
            <input
              name="email1"
              value={person1.email}
              placeholder="Email"
              type="text"
              onChange={handleChange1}
            />
          </div>
          <div>
            <input
              name="name2"
              value={person2.name}
              placeholder="Name"
              type="text"
              onChange={handleChange2}
            />
            <br />
            <input
              name="email2"
              value={person2.email}
              placeholder="Email"
              type="text"
              onChange={handleChange2}
            />
          </div>
        </div>
        <button>RSVP</button>
      </form>
      <GuestList guests={guests} />
    </div>
  );
};

export default Rsvp;
