import React, { useEffect } from "react";
import axios from "axios";

const GuestList = (props) => {
  const { guests, setGuests } = props;

  // const getGuests = () => {
  //   axios
  //     .get("https://barcelona22.herokuapp.com/rsvps")
  //     .then((res) => {
  //       console.log(res.data)
  //       setGuests(res.data);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // };

  useEffect(() => {
    // getGuests();
    axios
      .get("https://barcelona22.herokuapp.com/rsvps")
      .then((res) => {
        console.log(res.data);
        setGuests(res.data);
        console.log(guests)
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className="guestlist">
      <p>Guests</p>
      {guests &&
        guests.map((guest) => {
          return <h5>{guest}</h5>;
        })}
    </div>
  );
};

export default GuestList;
