import React from "react";
import {
  Accordion,
  AccordionItem,
  AccordionItemButton,
  AccordionItemHeading,
  AccordionItemPanel,
} from "react-accessible-accordion";

const Faqs = (props) => {
  const { questions } = props;
  return (
    <div className="faqs">
      {/* <Accordion className="acc">
        {questions.map((item) => {
          return (
            <AccordionItem className='accItem'>
              <AccordionItemHeading className='accHead'>
                <AccordionItemButton className='accButton'>{item.question}</AccordionItemButton>
              </AccordionItemHeading>
              <AccordionItemPanel className="accPanel">
                {item.answer}
              </AccordionItemPanel>
            </AccordionItem>
          );
        })}
      </Accordion> */}

      {questions.map((item))}
      <div className="acc">
        <button className="topic">
          <p className="question">{item.question}</p>
        </button>
        <div className="accordion__content">
          <div
            className="accordion__text"
            dangerouslySetInnerHTML={{ __html: item.answer }}
          />
        </div>
      </div>

      <div>
        <img
          className="faqsimage"
          href="https://imgur.com/a/kV37dMu"
          alt="alt"
        />
      </div>
    </div>
  );
};

export default Faqs;
